// cSpell:ignore Botao
import React from 'react'
import { SafeAreaView, Text, StyleSheet, Image } from 'react-native'
import { FontAwesome } from '@expo/vector-icons'
import * as Speech from 'expo-speech'

export default function App() {

  let frases = [
    "A imaginação é mais importante que o conhecimento.",
    "A liberação da energia atômica mudou tudo, menos nossa maneira de pensar.",
    "O mundo não está ameaçado pelas pessoas más, e sim por aquelas que permitem a maldade.",
    "O nacionalismo é uma doença infantil; é o sarampo da humanidade.",
    "Os ideais que iluminaram o meu caminho são a bondade, a beleza e a verdade.",
    "Nenhum cientista pensa com fórmulas.",
    "Tudo deveria se tornar o mais simples possível,mas não simplificado.",
    "Quero conhecer os pensamentos de Deus... O resto é detalhe.",
    "Duas coisas são infinitas: o universo e a estupidez humana. Mas, em relação ao universo, ainda não tenho certeza absoluta."
  ]

  function falar() {
    var frase = frases[parseInt(Math.random() * frases.length)]
    Speech.speak(frase, { language: 'pt' })
  }

  function parar(){
    Speech.stop()
  }

  return (
    <SafeAreaView
      style={styles.Principal}>
      <Text
        style={styles.Titulo}>
        <FontAwesome
          name='comment' size={30} />
        {" "}Fala Einstein!
        </Text>

      <Image
        style={styles.Imagem}
        source={require('./assets/einstein.png')} />

      <FontAwesome.Button
        name='volume-up'
        backgroundColor='#0275d8'
        style={styles.Botao}
        onPress={falar}>
        Ouvir a frase
        </FontAwesome.Button>

        <FontAwesome.Button
        name='stop-circle'
        backgroundColor='#d9534f'
        style={styles.Botao}
        onPress={parar}>
        Parar frase
        </FontAwesome.Button>
    </SafeAreaView>
  )
}

const styles = StyleSheet.create({
  Principal: {
    flex: 1,
    flexDirection: 'column',
    justifyContent: 'space-around',
    alignItems: 'center'
  },
  Titulo: {
    fontSize: 30,
    color: "#1a237e", /* azul indigo */
  },
  Imagem: {
    height: 500,
    resizeMode: 'center'
  },
  Botao: {
    height: 50,
    borderRadius: 50
  }
})
